转义特殊字符为html实体 123HtmlEncode: function(str)&#123; return str.replace(/&amp;/g, '&amp;').replace(/"/g, '"').replace(/&lt;/g, '&lt;').replace(/&gt;/g, '&gt;').replace(/'/g, ''');&#125;, 验证是否为有效的手机电话号码 1234IsMobile: function(str)&#123; var rp = /^1[3|4|5|7|8][0-9]\d&#123;4,8&#125;$/; return rp.test(str);&#125;, 验证是否为有效的座机电话号码 1234IsTel: function(str)&#123; var rp = /^([0-9]&#123;3,4&#125;-)?[0-9]&#123;7,8&#125;$/; return rp.test(str);&#125;, 判断是那种类型的浏览器 123456789101112131415161718192021222324252627282930313233343536373839404142434445WhichBrowser: function()&#123; var userAgent = navigator.userAgent; var isOpera = userAgent.indexOf("Opera") &gt; -1; var isIE = userAgent.indexOf("compatible") &gt; -1 &amp;&amp; userAgent.indexOf("MSIE") &gt; -1 &amp;&amp; !isOpera; var isFF = userAgent.indexOf("Firefox") &gt; -1; var isCH = userAgent.indexOf("Chrome") &gt; -1; var isSafari = userAgent.indexOf("Safari") &gt; -1; if (isIE)&#123; var IE5 = IE55 = IE6 = IE7 = IE8 = false; var reIE = new RegExp("MSIE (\d+\.\d+);"); reIE.test(userAgent); var fIEVersion = parseFloat(RegExp["$1"]); IE55 = fIEVersion == 5.5; IE6 = fIEVersion == 6.0; IE7 = fIEVersion == 7.0; IE8 = fIEVersion == 8.0; if (IE55) &#123; return "IE55"; &#125; if (IE6) &#123; return "IE6"; &#125; if (IE7) &#123; return "IE7"; &#125; if (IE8) &#123; return "IE8"; &#125; &#125; if (isFF) &#123; return "Firefox"; &#125; if (isCH) &#123; return "Chrome"; &#125; if (isOpera) &#123; return "Opera"; &#125; if (isSafari) &#123; return "Safari"; &#125;&#125;, 获取客户端浏览器cookie 123456789101112GetCookie: function(c_name)&#123; if(document.cookie.length&gt;0)&#123; c_start = document.cookie.indexOf(c_name + '='); if(c_start != -1)&#123; c_start = c_start + c_name.length + 1; c_end = document.cookie.indexOf(';',c_start); if (c_end==-1) c_end = document.cookie.length; return unescape(document.cookie.substring(c_start,c_end)); &#125; &#125; return '';&#125;, 设置客户端浏览器cookie 123456SetCookie: function(c_name, value, expiredays)&#123; var exdate = new Date(); exdate.setDate(exdate.getDate() + expiredays); document.cookie = c_name + "=" + escape(value) + ((expiredays==null) ? "" : ";expires="+exdate.toGMTString());&#125;,&#125;; 字符串长度截取 1234567891011121314151617181920function cutstr(str, len) &#123; var temp, icount = 0, patrn = /[^\x00-\xff]/， strre = ""; for (var i = 0; i &lt; str.length; i++) &#123; if (icount &lt; len - 1) &#123; temp = str.substr(i, 1); if (patrn.exec(temp) == null) &#123; icount = icount + 1 &#125; else &#123; icount = icount + 2 &#125; strre += temp &#125; else &#123; break; &#125; &#125; return strre + "..."&#125; 替换全部 123String.prototype.replaceAll = function(s1, s2) &#123; return this.replace(new RegExp(s1, "gm"), s2)&#125; 清除空格 1234String.prototype.trim = function() &#123; var reExtraSpace = /^\s*(.*?)\s+$/; return this.replace(reExtraSpace, "$1")&#125; 清除左空格/右空格 12function ltrim(s)&#123; return s.replace( /^(\s*| *)/, ""); &#125; function rtrim(s)&#123; return s.replace( /(\s*| *)$/, ""); &#125; 判断是否以某个字符串开头 123String.prototype.startWith = function (s) &#123; return this.indexOf(s) == 0&#125; 判断是否以某个字符串结束 1234String.prototype.endWith = function (s) &#123; var d = this.length - s.length; return (d &gt;= 0 &amp;&amp; this.lastIndexOf(s) == d)&#125; 转义html标签 123function HtmlEncode(text) &#123; return text.replace(/&amp;/g, '&amp;').replace(/"/g, '"').replace(/&lt;/g, '&lt;').replace(/&gt;/g, '&gt;')&#125; 时间日期格式转换 123456789101112131415161718Date.prototype.Format = function(formatStr) &#123; var str = formatStr; var Week = ['日', '一', '二', '三', '四', '五', '六']; str = str.replace(/yyyy|YYYY/, this.getFullYear()); str = str.replace(/yy|YY/, (this.getYear() % 100) &gt; 9 ? (this.getYear() % 100).toString() : '0' + (this.getYear() % 100)); str = str.replace(/MM/, (this.getMonth() + 1) &gt; 9 ? (this.getMonth() + 1).toString() : '0' + (this.getMonth() + 1)); str = str.replace(/M/g, (this.getMonth() + 1)); str = str.replace(/w|W/g, Week[this.getDay()]); str = str.replace(/dd|DD/, this.getDate() &gt; 9 ? this.getDate().toString() : '0' + this.getDate()); str = str.replace(/d|D/g, this.getDate()); str = str.replace(/hh|HH/, this.getHours() &gt; 9 ? this.getHours().toString() : '0' + this.getHours()); str = str.replace(/h|H/g, this.getHours()); str = str.replace(/mm/, this.getMinutes() &gt; 9 ? this.getMinutes().toString() : '0' + this.getMinutes()); str = str.replace(/m/g, this.getMinutes()); str = str.replace(/ss|SS/, this.getSeconds() &gt; 9 ? this.getSeconds().toString() : '0' + this.getSeconds()); str = str.replace(/s|S/g, this.getSeconds()); return str&#125; 判断是否为数字类型 12345678function isDigit(value) &#123; var patrn = /^[0-9]*$/; if (patrn.exec(value) == null || value == "") &#123; return false &#125; else &#123; return true &#125;&#125; 设置cookie值 123456789function setCookie(name, value, Hours) &#123; var d = new Date(); var offset = 8; var utc = d.getTime() + (d.getTimezoneOffset() * 60000); var nd = utc + (3600000 * offset); var exp = new Date(nd); exp.setTime(exp.getTime() + Hours * 60 * 60 * 1000); document.cookie = name + "=" + escape(value) + ";path=/;expires=" + exp.toGMTString() + ";domain=360doc.com;"&#125; 获取cookie值 12345function getCookie(name) &#123; var arr = document.cookie.match(new RegExp("(^| )" + name + "=([^;]*)(;|$)")); if (arr != null) return unescape(arr[2]); return null&#125; 加入收藏夹 1234567891011function AddFavorite(sURL, sTitle) &#123; try &#123; window.external.addFavorite(sURL, sTitle) &#125; catch(e) &#123; try &#123; window.sidebar.addPanel(sTitle, sURL, "") &#125; catch(e) &#123; alert("加入收藏失败，请使用Ctrl+D进行添加") &#125; &#125;&#125; 设为首页 12345678910111213141516function setHomepage() &#123; if (document.all) &#123; document.body.style.behavior = 'url(#default#homepage)'; document.body.setHomePage('http://w3cboy.com') &#125; else if (window.sidebar) &#123; if (window.netscape) &#123; try &#123; netscape.security.PrivilegeManager.enablePrivilege("UniversalXPConnect") &#125; catch(e) &#123; alert("该操作被浏览器拒绝，如果想启用该功能，请在地址栏内输入 about:config,然后将项 signed.applets.codebase_principal_support 值该为true") &#125; &#125; var prefs = Components.classes['@mozilla.org/preferences-service;1'].getService(Components.interfaces.nsIPrefBranch); prefs.setCharPref('browser.startup.homepage', 'http://w3cboy.com') &#125;&#125; 加载样式文件 123456789101112function LoadStyle(url) &#123; try &#123; document.createStyleSheet(url) &#125; catch(e) &#123; var cssLink = document.createElement('link'); cssLink.rel = 'stylesheet'; cssLink.type = 'text/css'; cssLink.href = url; var head = document.getElementsByTagName('head')[0]; head.appendChild(cssLink) &#125;&#125; 返回脚本内容 123456789101112131415161718function evalscript(s) &#123; if(s.indexOf('&lt;script') == -1) return s; var p = /&lt;script[^\&gt;]*?&gt;([^\x00]*?)&lt;/script&gt;/ig; var arr = []; while(arr = p.exec(s)) &#123; var p1 = /&lt;script[^\&gt;]*?src="([^\&gt;]*?)"[^\&gt;]*?(reload="1")?(?:charset="([\w\-]+?)")?&gt;&lt;/script&gt;/i; var arr1 = []; arr1 = p1.exec(arr[0]); if(arr1) &#123; appendscript(arr1[1], '', arr1[2], arr1[3]); &#125; else &#123; p1 = /&lt;script(.*?)&gt;([^\x00]+?)&lt;/script&gt;/i; arr1 = p1.exec(arr[0]); appendscript('', arr1[2], arr1[1].indexOf('reload=') != -1); &#125; &#125; return s;&#125; 清除脚本内容 123function stripscript(s) &#123; return s.replace(/&lt;script.*?&gt;.*?&lt;/script&gt;/ig, '');&#125; 动态加载脚本文件 1234567891011121314151617181920212223242526272829303132function appendscript(src, text, reload, charset) &#123; var id = hash(src + text); if(!reload &amp;&amp; in_array(id, evalscripts)) return; if(reload &amp;&amp; $(id)) &#123; $(id).parentNode.removeChild($(id)); &#125; evalscripts.push(id); var scriptNode = document.createElement("script"); scriptNode.type = "text/javascript"; scriptNode.id = id; scriptNode.charset = charset ? charset : (BROWSER.firefox ? document.characterSet : document.charset); try &#123; if(src) &#123; scriptNode.src = src; scriptNode.onloadDone = false; scriptNode.onload = function () &#123; scriptNode.onloadDone = true; JSLOADED[src] = 1; &#125;; scriptNode.onreadystatechange = function () &#123; if((scriptNode.readyState == 'loaded' || scriptNode.readyState == 'complete') &amp;&amp; !scriptNode.onloadDone) &#123; scriptNode.onloadDone = true; JSLOADED[src] = 1; &#125; &#125;; &#125; else if(text)&#123; scriptNode.text = text; &#125; document.getElementsByTagName('head')[0].appendChild(scriptNode); &#125; catch(e) &#123;&#125;&#125; 返回按ID检索的元素对象 123function $(id) &#123; return !id ? null : document.getElementById(id);&#125; 跨浏览器绑定事件 12345678910function addEventSamp(obj,evt,fn)&#123; if(!oTarget)&#123;return;&#125; if (obj.addEventListener) &#123; obj.addEventListener(evt, fn, false); &#125;else if(obj.attachEvent)&#123; obj.attachEvent('on'+evt,fn); &#125;else&#123; oTarget["on" + sEvtType] = fn; &#125; &#125; 跨浏览器删除事件 12345678910function delEvt(obj,evt,fn)&#123; if(!obj)&#123;return;&#125; if(obj.addEventListener)&#123; obj.addEventListener(evt,fn,false); &#125;else if(oTarget.attachEvent)&#123; obj.attachEvent("on" + evt,fn); &#125;else&#123; obj["on" + evt] = fn; &#125;&#125; 为元素添加on方法 12345678Element.prototype.on = Element.prototype.addEventListener; NodeList.prototype.on = function (event, fn) &#123;、 []['forEach'].call(this, function (el) &#123; el.on(event, fn); &#125;); return this;&#125;; 为元素添加trigger方法 12345678910111213141516Element.prototype.trigger = function (type, data) &#123; var event = document.createEvent('HTMLEvents'); event.initEvent(type, true, true); event.data = data || &#123;&#125;; event.eventName = type; event.target = this; this.dispatchEvent(event); return this;&#125;; NodeList.prototype.trigger = function (event) &#123; []['forEach'].call(this, function (el) &#123; el['trigger'](event); &#125;); return this;&#125;; 检验URL链接是否有效 12345678910111213141516171819function getUrlState(URL)&#123; var xmlhttp = new ActiveXObject("microsoft.xmlhttp"); xmlhttp.Open("GET",URL, false); try&#123; xmlhttp.Send(); &#125;catch(e)&#123; &#125;finally&#123; var result = xmlhttp.responseText; if(result)&#123; if(xmlhttp.Status==200)&#123; return(true); &#125;else&#123; return(false); &#125; &#125;else&#123; return(false); &#125; &#125;&#125; 格式化CSS样式代码 123456789function formatCss(s)&#123;//格式化代码 s = s.replace(/\s*([\&#123;\&#125;\:\;\,])\s*/g, "$1"); s = s.replace(/;\s*;/g, ";"); //清除连续分号 s = s.replace(/\,[\s\.\#\d]*&#123;/g, "&#123;"); s = s.replace(/([^\s])\&#123;([^\s])/g, "$1 &#123;
	$2"); s = s.replace(/([^\s])\&#125;([^
